// –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –æ—Ç—á–µ—Ç–∞ –æ —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏–∏
const response = $input.all()[0].json;

const timestamp = new Date().toLocaleString('ru-RU', { timeZone: 'Asia/Tbilisi' });

let reportText = `üìä –°–ò–ù–•–†–û–ù–ò–ó–ê–¶–ò–Ø –ë–†–û–ù–ò–†–û–í–ê–ù–ò–ô\n`;
reportText += `–í—Ä–µ–º—è: ${timestamp}\n\n`;

if (response && response.success) {
  const summary = response.summary || {};
  reportText += `‚úÖ –°—Ç–∞—Ç—É—Å: –£—Å–ø–µ—à–Ω–æ\n`;
  reportText += `üì¶ –í—Å–µ–≥–æ –æ–±—Ä–∞–±–æ—Ç–∞–Ω–æ: ${summary.total_bookings || 0} –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–π\n`;
  reportText += `‚ûï –°–æ–∑–¥–∞–Ω–æ –Ω–æ–≤—ã—Ö: ${summary.total_created || 0}\n`;
  reportText += `üîÑ –û–±–Ω–æ–≤–ª–µ–Ω–æ: ${summary.total_updated || 0}\n`;
  reportText += `‚ùå –û—à–∏–±–æ–∫: ${summary.total_errors || 0}\n\n`;

  reportText += `üìã –ü–æ —Ñ–∏–ª–∏–∞–ª–∞–º:\n`;
  if (response.per_branch && Array.isArray(response.per_branch)) {
    response.per_branch.forEach(branch => {
      const status = (branch.errors || 0) === 0 ? '‚úÖ' : '‚ö†Ô∏è';
      reportText += `${status} ${(branch.branch || 'unknown').toUpperCase()}: ${branch.total || 0} (—Å–æ–∑–¥–∞–Ω–æ: ${branch.created || 0}, –æ–±–Ω–æ–≤–ª–µ–Ω–æ: ${branch.updated || 0}, –æ—à–∏–±–æ–∫: ${branch.errors || 0})\n`;
    });
  }

  if ((summary.total_errors || 0) > 0) {
    reportText += `\n‚ö†Ô∏è –û–±–Ω–∞—Ä—É–∂–µ–Ω–æ ${summary.total_errors} –æ—à–∏–±–æ–∫. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ª–æ–≥–∏ –¥–ª—è –¥–µ—Ç–∞–ª–µ–π.`;
  }
} else {
  reportText += `‚ùå –°—Ç–∞—Ç—É—Å: –û—à–∏–±–∫–∞\n`;
  reportText += `–û—à–∏–±–∫–∞: ${response?.error || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞'}\n`;
}

return {
  json: {
    reportText,
    success: response?.success || false,
    total: response?.summary?.total_bookings || 0,
    errors: response?.summary?.total_errors || 0
  }
};
