// –ü–∞—Ä—Å–∏–Ω–≥ –∏ –≤–∞–ª–∏–¥–∞—Ü–∏—è –≤–µ–±—Ö—É–∫–∞ –æ—Ç AmoCRM
const crypto = require('crypto');

try {
  const body = $input.item.json.body || {};
  const headers = $input.item.json.headers || {};

  // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è request_id –∏ event_hash
  const requestId = headers['x-request-id'] || headers['x-webhook-id'] || 
                    (crypto.randomUUID ? crypto.randomUUID() : Date.now().toString());

  let amocrmEntityId = null;
  let entityType = 'unknown';
  let eventType = 'unknown';
  let isKnownFormat = false;
  let validationErrors = [];
  let parsedPayload = {};
  let parseError = false;
  let errorMessage = '';
  let eventHash = '';

  // ========== –®–ê–ì 0: –ì–µ–Ω–µ—Ä–∞—Ü–∏—è event_hash –¥–ª—è –¥–µ–¥—É–ø–ª–∏–∫–∞—Ü–∏–∏ ==========
  try {
    const hashData = JSON.stringify({
      'x-request-id': headers['x-request-id'] || '',
      'x-webhook-id': headers['x-webhook-id'] || '',
      timestamp: headers['x-timestamp'] || Date.now().toString(),
      payload: JSON.stringify(body)
    });
    eventHash = crypto.createHash('sha256').update(hashData).digest('hex');
  } catch (e) {
    eventHash = crypto.createHash('sha256')
      .update((JSON.stringify(body) || '') + Date.now())
      .digest('hex');
  }

  // ========== –®–ê–ì 1: –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Ç–∏–ø–∞ —Å–æ–±—ã—Ç–∏—è ==========
  // AmoCRM –æ—Ç–ø—Ä–∞–≤–ª—è–µ—Ç —Å–æ–±—ã—Ç–∏—è –≤ —Ñ–æ—Ä–º–∞—Ç–µ: { account: {...}, leads: [...], contacts: [...] }
  // –¢–∏–ø —Å–æ–±—ã—Ç–∏—è –æ–ø—Ä–µ–¥–µ–ª—è–µ—Ç—Å—è –ø–æ –Ω–∞–ª–∏—á–∏—é –ø–æ–ª–µ–π –≤ payload

  const knownEventTypes = [
    'lead.add',           // –°–¥–µ–ª–∫–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∞
    'lead.update',        // –°–¥–µ–ª–∫–∞ –∏–∑–º–µ–Ω–µ–Ω–∞
    'lead.status',        // –°—Ç–∞—Ç—É—Å —Å–¥–µ–ª–∫–∏ –∏–∑–º–µ–Ω–µ–Ω
    'lead.delete',        // –°–¥–µ–ª–∫–∞ —É–¥–∞–ª–µ–Ω–∞
    'contact.add',        // –ö–æ–Ω—Ç–∞–∫—Ç –¥–æ–±–∞–≤–ª–µ–Ω
    'contact.update',     // –ö–æ–Ω—Ç–∞–∫—Ç –∏–∑–º–µ–Ω–µ–Ω
    'contact.delete',     // –ö–æ–Ω—Ç–∞–∫—Ç —É–¥–∞–ª–µ–Ω
    'company.add',        // –ö–æ–º–ø–∞–Ω–∏—è –¥–æ–±–∞–≤–ª–µ–Ω–∞
    'company.update',     // –ö–æ–º–ø–∞–Ω–∏—è –∏–∑–º–µ–Ω–µ–Ω–∞
    'company.delete',     // –ö–æ–º–ø–∞–Ω–∏—è —É–¥–∞–ª–µ–Ω–∞
    'customer.add',       // –ü–æ–∫—É–ø–∞—Ç–µ–ª—å –¥–æ–±–∞–≤–ª–µ–Ω
    'customer.update',    // –ü–æ–∫—É–ø–∞—Ç–µ–ª—å –∏–∑–º–µ–Ω–µ–Ω
    'task.add',           // –ó–∞–¥–∞—á–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∞
    'task.update',        // –ó–∞–¥–∞—á–∞ –∏–∑–º–µ–Ω–µ–Ω–∞
    'unsorted.add',       // –ù–µ—Ä–∞–∑–æ–±—Ä–∞–Ω–Ω–æ–µ –¥–æ–±–∞–≤–ª–µ–Ω–æ
    'unsorted.delete',    // –ù–µ—Ä–∞–∑–æ–±—Ä–∞–Ω–Ω–æ–µ —É–¥–∞–ª–µ–Ω–æ
    'note.add',           // –ü—Ä–∏–º–µ—á–∞–Ω–∏–µ –¥–æ–±–∞–≤–ª–µ–Ω–æ
    'message.add',        // –í—Ö–æ–¥—è—â–µ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –¥–æ–±–∞–≤–ª–µ–Ω–æ
    'conversation.add'    // –ë–µ—Å–µ–¥–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∞
  ];

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–∏–ø —Å–æ–±—ã—Ç–∏—è –ø–æ —Å—Ç—Ä—É–∫—Ç—É—Ä–µ payload
  if (body.leads && body.leads.length > 0) {
    const lead = body.leads[0];
    amocrmEntityId = lead.id ? String(lead.id) : null;

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å—Ç–∞—Ç—É—Å –∏–∑–º–µ–Ω–µ–Ω–∏—è
    if (lead.status_id !== undefined) {
      eventType = 'lead.status';
      entityType = 'lead';
    } else if (lead.is_deleted) {
      eventType = 'lead.delete';
      entityType = 'lead';
    } else {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —ç—Ç–æ —Å–æ–∑–¥–∞–Ω–∏–µ –∏–ª–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ (–ø–æ timestamp –∏–ª–∏ –¥—Ä—É–≥–∏–º –ø—Ä–∏–∑–Ω–∞–∫–∞–º)
      // –ï—Å–ª–∏ –µ—Å—Ç—å updated_at –∏ created_at –±–ª–∏–∑–∫–∏ - —ç—Ç–æ —Å–æ–∑–¥–∞–Ω–∏–µ
      const createdAt = lead.created_at || 0;
      const updatedAt = lead.updated_at || 0;
      if (updatedAt - createdAt < 5) {
        eventType = 'lead.add';
      } else {
        eventType = 'lead.update';
      }
      entityType = 'lead';
    }
    parsedPayload = lead;
  } else if (body.contacts && body.contacts.length > 0) {
    const contact = body.contacts[0];
    amocrmEntityId = contact.id ? String(contact.id) : null;

    if (contact.is_deleted) {
      eventType = 'contact.delete';
      entityType = 'contact';
    } else {
      const createdAt = contact.created_at || 0;
      const updatedAt = contact.updated_at || 0;
      if (updatedAt - createdAt < 5) {
        eventType = 'contact.add';
      } else {
        eventType = 'contact.update';
      }
      entityType = 'contact';
    }
    parsedPayload = contact;
  } else if (body.companies && body.companies.length > 0) {
    const company = body.companies[0];
    amocrmEntityId = company.id ? String(company.id) : null;

    if (company.is_deleted) {
      eventType = 'company.delete';
      entityType = 'company';
    } else {
      const createdAt = company.created_at || 0;
      const updatedAt = company.updated_at || 0;
      if (updatedAt - createdAt < 5) {
        eventType = 'company.add';
      } else {
        eventType = 'company.update';
      }
      entityType = 'company';
    }
    parsedPayload = company;
  } else if (body.customers && body.customers.length > 0) {
    const customer = body.customers[0];
    amocrmEntityId = customer.id ? String(customer.id) : null;

    const createdAt = customer.created_at || 0;
    const updatedAt = customer.updated_at || 0;
    if (updatedAt - createdAt < 5) {
      eventType = 'customer.add';
    } else {
      eventType = 'customer.update';
    }
    entityType = 'customer';
    parsedPayload = customer;
  } else if (body.tasks && body.tasks.length > 0) {
    const task = body.tasks[0];
    amocrmEntityId = task.id ? String(task.id) : null;

    const createdAt = task.created_at || 0;
    const updatedAt = task.updated_at || 0;
    if (updatedAt - createdAt < 5) {
      eventType = 'task.add';
    } else {
      eventType = 'task.update';
    }
    entityType = 'task';
    parsedPayload = task;
  } else if (body.unsorted && body.unsorted.length > 0) {
    const unsorted = body.unsorted[0];
    amocrmEntityId = unsorted.uid ? String(unsorted.uid) : null;

    if (unsorted.is_deleted) {
      eventType = 'unsorted.delete';
    } else {
      eventType = 'unsorted.add';
    }
    entityType = 'unsorted';
    parsedPayload = unsorted;
  } else if (body.notes && body.notes.length > 0) {
    const note = body.notes[0];
    amocrmEntityId = note.id ? String(note.id) : null;
    eventType = 'note.add';
    entityType = 'note';
    parsedPayload = note;
  } else if (body.messages && body.messages.length > 0) {
    const message = body.messages[0];
    amocrmEntityId = message.id ? String(message.id) : null;
    eventType = 'message.add';
    entityType = 'message';
    parsedPayload = message;
  } else if (body.conversations && body.conversations.length > 0) {
    const conversation = body.conversations[0];
    amocrmEntityId = conversation.id ? String(conversation.id) : null;
    eventType = 'conversation.add';
    entityType = 'conversation';
    parsedPayload = conversation;
  } else {
    validationErrors.push('–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∞ payload: –Ω–µ—Ç –∏–∑–≤–µ—Å—Ç–Ω—ã—Ö —Å—É—â–Ω–æ—Å—Ç–µ–π (leads, contacts, companies, etc.)');
  }

  // ========== –®–ê–ì 2: –í–∞–ª–∏–¥–∞—Ü–∏—è –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã—Ö –ø–æ–ª–µ–π ==========
  if (entityType !== 'unknown' && !amocrmEntityId) {
    validationErrors.push(`–û—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ–µ –ø–æ–ª–µ id –¥–ª—è ${entityType}`);
  }

  // ========== –ò–¢–û–ì–û–í–ê–Ø –ü–†–û–í–ï–†–ö–ê ==========
  if (
    Object.keys(parsedPayload).length > 0 &&
    amocrmEntityId !== null &&
    eventType !== 'unknown' &&
    entityType !== 'unknown' &&
    validationErrors.length === 0
  ) {
    isKnownFormat = true;
  } else {
    isKnownFormat = false;
  }

  return {
    json: {
      ...$input.item.json,
      requestId: requestId,
      eventHash: eventHash,
      amocrmEntityId: amocrmEntityId,
      eventType: eventType,
      entityType: entityType,
      isKnownFormat: isKnownFormat,
      parsedPayload: parsedPayload,
      validationErrors: validationErrors,
      parseError: parseError,
      errorMessage: errorMessage,
      account: body.account || null
    }
  };
} catch (error) {
  // –ö—Ä–∏—Ç–∏—á–µ—Å–∫–∞—è –æ—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞
  const body = $input.item.json.body || {};
  const headers = $input.item.json.headers || {};

  let eventHash = '';
  try {
    const hashData = JSON.stringify({
      'x-request-id': headers['x-request-id'] || '',
      timestamp: Date.now().toString(),
      payload: JSON.stringify(body)
    });
    eventHash = crypto.createHash('sha256').update(hashData).digest('hex');
  } catch {
    eventHash = crypto.createHash('sha256')
      .update((JSON.stringify(body) || '') + Date.now())
      .digest('hex');
  }

  return {
    json: {
      ...$input.item.json,
      requestId: headers['x-request-id'] || Date.now().toString(),
      eventHash: eventHash,
      amocrmEntityId: null,
      eventType: 'unknown',
      entityType: 'unknown',
      isKnownFormat: false,
      parsedPayload: {},
      validationErrors: ['–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∞—è –æ—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞: ' + (error.message || String(error))],
      parseError: true,
      errorMessage: error.message || String(error),
      errorStack: error.stack || ''
    }
  };
}

