const branch = $json.branch;
const token = $json.token;

const items = [];
for (let page = 1; page <= 3; page++) {
  items.push({
    json: { branch, token, page }
  });
}

return items;

const branch = $('Prepare 3 Pages').first().json.branch;
const operations = $json.operations?.data || $json.data || [];

if (operations.length === 0) {
  return [{ json: { branch, status: 'no_data' } }];
}

const historyItems = operations.map(item => {
  const op = item.attributes || item;
  return {
    json: {
      branch,
      operation_type: op.operation_type || op.type || 'unknown',
      operation_id: op.id ? String(op.id) : null,
      description: op.description || op.message || '',
      entity_type: op.entity_type || op.object_type || null,
      entity_id: op.entity_id ? String(op.entity_id) : null,
      user_name: op.user_name || op.user || op.author || null,
      created_at: op.created_at || op.timestamp || new Date().toISOString(),
      raw_data: JSON.stringify(op)
    }
  };
});

return historyItems;

const saved = $input.all();
const successCount = saved.filter(s => !s.json.error).length;
const errorCount = saved.filter(s => s.json.error).length;

// –ê–≥—Ä–µ–≥–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ —Ñ–∏–ª–∏–∞–ª–∞–º
const byBranch = {};
saved.forEach(item => {
  if (item.json.branch) {
    if (!byBranch[item.json.branch]) {
      byBranch[item.json.branch] = { success: 0, error: 0 };
    }
    if (item.json.error) {
      byBranch[item.json.branch].error++;
    } else {
      byBranch[item.json.branch].success++;
    }
  }
});

let message = `üìú HISTORY PARSER\n`;
