  const stats = byBranch[branch];
  message += `${branch.toUpperCase()}: ${stats.success} ‚úì`;
  if (stats.error > 0) {
    message += ` / ${stats.error} ‚úó`;
  }
  message += `\n`;
});
message += `\n–í—Å–µ–≥–æ: ${successCount} / ${saved.length}`;

return [{ json: { message, success: errorCount === 0, saved_count: successCount, error_count: errorCount, by_branch: byBranch } }];
