// –û–±—Ä–∞–±–æ—Ç–∫–∞ –æ—Ç–≤–µ—Ç–∞ –æ—Ç TBC Bank API (–ø—Ä—è–º–æ–π –∑–∞–ø—Ä–æ—Å)
const apiResponse = $json;

console.log('Processing TBC Bank API response...');

try {
  // API –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç: { iso1: 'RUR', iso2: 'GEL', buyRate: 0.0289, sellRate: 0.0402, ... }
  if (!apiResponse.sellRate) {
    return [{
      json: {
        status: 'error',
        error: 'Invalid API response: sellRate not found',
        suggestion: 'TBC Bank API structure may have changed'
      }
    }];
  }

  const returnRate = apiResponse.sellRate; // –ö—É—Ä—Å –ø—Ä–æ–¥–∞–∂–∏ —Ä—É–±–ª—è (GEL –∑–∞ 1 RUB)
  const buyRate = apiResponse.buyRate; // –ö—É—Ä—Å –ø–æ–∫—É–ø–∫–∏ —Ä—É–±–ª—è (–¥–ª—è —Å–ø—Ä–∞–≤–∫–∏)

  if (!returnRate || returnRate <= 0 || returnRate >= 1) {
    return [{
      json: {
        status: 'error',
        error: `Invalid exchange rate: ${returnRate}`,
        suggestion: 'Rate should be between 0 and 1 (GEL per 1 RUB)'
      }
    }];
  }

  console.log('Parsed return rate:', { returnRate, buyRate });

  return [{
    json: {
      tbc_return_rate: returnRate,
      raw_data: JSON.stringify({
        parsed_at: new Date().toISOString(),
        source: 'apigw.tbcbank.ge/api/v1/exchangeRates/getExchangeRate',
        method: 'api',
        return_rate: returnRate,
        buy_rate: buyRate,
        update_date: apiResponse.updateDate,
        full_response: apiResponse
      })
    }
  }];

} catch (error) {
  console.error('Parse error:', error);
  return [{
    json: {
      status: 'error',
      error: error.message
    }
  }];
}

const saved = $('Save to DB').first()?.json;

const rate = saved?.tbc_return_rate;

let message = `üí± TBC BANK RETURN RATE\n\n`;

if (rate) {
  message += `–ö—É—Ä—Å –≤–æ–∑–≤—Ä–∞—Ç–∞ (GEL –∑–∞ 1 RUB): ${rate.toFixed(6)}\n`;
  message += `–î–ª—è –≤–æ–∑–≤—Ä–∞—Ç–∞ 1000 GEL: ${(1000 / rate).toFixed(2)} RUB\n`;
}

message += `\n‚úÖ Saved at ${new Date().toISOString()}`;

return [{ json: { message } }];
